import React, { useState, useEffect } from 'react';
import { Container, Col, Form, Button, Card, CardColumns } from 'react-bootstrap';

import Auth from "../utils/auth";

  // create method to search for books and set state on form submit

  const SearchBooks = () => {
    // create state for holding returned google api data
    const [searchedBooks, setSearchedBooks] = useState([]);
    // create state for holding our search field data
    const [searchInput, setSearchInput] = useState("");

    // create state to hold saved bookId values
    const [savedBookIds, setSavedBookIds] = useState(getSavedBookIds());

    const [saveBook, { error }] = useMutation(SAVE_BOOK);

    // set up useEffect hook to save `savedBookIds` list to localStorage on component unmount
    // learn more here: https://reactjs.org/docs/hooks-effect.html#effects-with-cleanup
    useEffect(() => {
      return () => saveBookIds(savedBookIds);
    });

    // create method to search for books and set state on form submit
    const handleFormSubmit = async (event) => {
      event.preventDefault();

      if (!searchInput) {
        return false;
      }

      try {
        const response = await fetch(
          `https://www.googleapis.com/books/v1/volumes?q=${searchInput}`
        );

        if (!response.ok) {
          throw new Error("something went wrong!");
        }

        const { items } = await response.json();

        const bookData = items.map((book) => ({
          bookId: book.id,
          authors: book.volumeInfo.authors || ["No author to display"],
          title: book.volumeInfo.title,
          description: book.volumeInfo.description,
          image: book.volumeInfo.imageLinks?.thumbnail || "",
        }));

        setSearchedBooks(bookData);
        setSearchInput("");
      } catch (err) {
        console.error(err);
      }
    };

    // create function to handle saving a book to our database
    const handleSaveBook = async (bookId) => {
      // find the book in `searchedBooks` state by the matching id
      const bookToSave = searchedBooks.find((book) => book.bookId === bookId);

      // get token
      const token = Auth.loggedIn() ? Auth.getToken() : null;

      if (!token) {
        return false;
      }

      try {
        const { data } = await saveBook({
          variables: { bookData: { ...bookToSave } },
        });
        console.log(savedBookIds)
        // if book successfully saves to user's account, save book id to state
        setSavedBookIds([...savedBookIds, bookToSave.bookId]);
      } catch (err) {
        console.error(err);
      }
    };

    return (
        <>
        <div id="myCarousel" className="carousel slide carousel-fade" data-bs-ride="carousel">
            <div className="carousel-indicators">
                <button type="button" data-bs-target="#myCarousel" data-bs-slide-to="0" className="active" aria-current="true"
                    aria-label="Slide 1"></button>
                <button type="button" data-bs-target="#myCarousel" data-bs-slide-to="1" aria-label="Slide 2"></button>
            </div>
            <div className="carousel-inner">
                <div className="carousel-item active">
                    <div className="slide-one-bg" aria-hidden="true"></div>

                    <div className="container">
                        <div className="carousel-caption text-start">
                            <img className="" src=""
                                alt="" />
                            <p></p>
                        </div>
                    </div>
                </div>
                <div className="carousel-item">
                    <div className="slide-two-bg" aria-hidden="true"></div>

                    <div className="container">
                        <div className="carousel-caption text-start">
                            <img className="" src=""
                                alt="" />
                            <p></p>
                        </div>
                    </div>
                </div>
            </div>
        </div>

        <Container className='text-light bg-dark'>
          <h1>Search for Books!</h1>
          <Form onSubmit={handleFormSubmit}>
            <Form.Row>
              <Col xs={12} md={8}>
                <Form.Control
                  name='searchInput'
                  value={searchInput}
                  onChange={(e) => setSearchInput(e.target.value)}
                  type='text'
                  size='lg'
                  placeholder='Search for a book'
                />
              </Col>
              <Col xs={12} md={4}>
                <Button type='submit' variant='success' size='lg'>
                  Submit Search
                </Button>
              </Col>
            </Form.Row>
          </Form>
        </Container>
        <Container>
        <h2>
          {searchedBooks.length
            ? `Viewing ${searchedBooks.length} results:`
            : "Search for a book to begin"}
        </h2>
        <CardColumns>
          {searchedBooks.map((book) => {
            return (
              <Card key={book.bookId} border="dark">
                {book.image ? (
                  <Card.Img
                    src={book.image}
                    alt={`The cover for ${book.title}`}
                    variant="top"
                  />
                ) : null}
                <Card.Body>
                  <Card.Title>{book.title}</Card.Title>
                  <p className="small">Authors: {book.authors}</p>
                  <Card.Text>{book.description}</Card.Text>
                  {Auth.loggedIn() && (
                    <Button
                      disabled={savedBookIds?.some(
                        (savedBookId) => savedBookId === book.bookId
                      )}
                      className="btn-block btn-info"
                      onClick={() => handleSaveBook(book.bookId)}
                    >
                      {savedBookIds?.some(
                        (savedBookId) => savedBookId === book.bookId
                      )
                        ? "This book has already been saved!"
                        : "Save this Book!"}
                    </Button>
                  )}
                </Card.Body>
              </Card>
            );
          })}
        </CardColumns>
      </Container>
        </>
    );
}

export default Home;
